INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('1', 'Step 1Heat oven to 400Â°F. Heat oil in large ovenproof skillet on medium-high. Season chicken with 1/2 teaspoon salt and 1/4 teaspoon pepper and cook until deep golden brown on 1 side  4 to 5 minutes.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('1', 'Step 2Flip chicken and cookÂ 1 minute. Reduce heatÂ to medium  add balsamic vinegar and gently simmer until slightly thickened and syrupy Â 1 to 2 minutes. Transfer skilletÂ to oven and roast 10 minutes.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('1', 'Step 3Turn chicken to coat in vinegar  top with mozzarella and roast until chicken registers 165Â°F on instant-read thermometer and cheese begins to melt  about 2 minutes.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('1', 'Step 4Serve topped with basil leaves and tomato and sprinkle with salt and pepper. Drizzle with any additional balsamic glaze from skillet if desired.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('2', 'Step 1Heat grill to medium-high.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('2', 'Step 2In large bowl  toss chicken withÂ oil and 1/2 tsp each salt and pepperÂ to coat; fold in pineapple  peppers Â and onion. Thread onto skewers Â separating layers of onion andÂ using only 2 layers of onion atÂ a time between chicken  pineapple  and peppers.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('2', 'Step 3Grill  turning occasionally  until chicken is cooked through  8 to 12 minutes.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('3', 'Step 1Chop cilantro stems and add to mini food processor along with 1 cup cilantro leaves  green curry paste  coconut milk  lime zest  soy sauce and 1/2 tablespoon honey; puree until smooth. RemoveÂ 1/2 cup and add to small bowl; whisk in oil  lime juice  and remaining 1/2 tablespoons honey  then set aside for serving.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('3', 'Step 2Place chicken on rimmed baking sheet and drizzle with remaining marinade  using hands to rub all over chicken. Season chicken with 1/2 teaspoon salt. Let sit at room temp 1 hour.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('3', 'Step 3Meanwhile  rinse rice and soakÂ 20 minutes. Drain and cook per package directions  adding ginger  garlic  and 1/4 teaspoon salt. Remove ginger and garlic and spread cooked rice on second rimmed baking sheet lined with parchment. Stir in remaining cilantro leaves.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('3', 'Step 4Meanwhile  heat grill to medium. Liberally oil grill grates. Grill chicken breast side down until beginning to char  6 to 8 minutes  then flip  cover and grill until internal temp of thigh reaches 165Â°F  38 to 45 minutes more. Transfer to cutting board; let restÂ 10 minutes before carving into pieces.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('3', 'Step 5Meanwhile  grill lime wedges until charred  1 to 2 minutes per side. Serve chicken over rice with reserved sauce and grilled lime wedges.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('4', 'Step 1Heat 2 tablespoons canola oil in large skillet on medium-high. Add mushrooms  season with 1/4 teaspoon each salt and pepper  and cook  stirring occasionally  until golden brown  4 to 5 minutes. Transfer to plate.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('4', 'Step 2In same skillet  heat remaining 1 1/2 tablespoons canola oil on medium-high. Add chicken in single layer  then season with 1/4 teaspoon each salt and pepper. Cook undisturbed until golden brown  3 to 4 minutes. Turn  stir in garlic and ginger  and then cook  turning once or twice more and gently breaking up chicken  until cooked through  2 to 3 minutes.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('4', 'Step 3Meanwhile  in small bowl  stir together hoisin  soy sauce  vinegar  sriracha  and sesame oil.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('4', 'Step 4Add sauce to pan and stir until coated. Remove from heat  then fold in water chestnuts  scallions (white and light green parts)  and mushrooms  scraping up browned bits. Sprinkle with reserved scallion greens and sesame seeds. Spoon into lettuce leaves and serve immediately.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('5', 'Step 1Heat oven to 425Â°F. With mortar and pestle  coarsely crush coriander and cumin seeds. Stir in red pepper flakes  1/4 teaspoon sumac  and a pinch of salt.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('5', 'Step 2On rimmed baking sheet  toss tomatoes and garlic with 1 tablespoon oil and spices. Roast 12 minutes.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('5', 'Step 3Meanwhile  heat 1 teaspoon oil in small skillet on medium. Season chicken with 1/4 teaspoon each salt and pepper and cook until golden brown  5 to 6 minutes. Turn and cook 1 minute.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('5', 'Step 4Toss tomato mixture with chickpeas  nestle chicken among tomatoes  and continue roasting until chicken is cooked through  6 to 8 minutes more. Transfer chicken to cutting board and let rest 5 minutes before slicing.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('5', 'Step 5 In bowl  toss farro  scallion  vinegar  remaining 1 tablespoon oil  and a pinch each of salt and pepper. Fold in chickpea-tomato mixture and chicken  then greens.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('6', 'Step 1In medium bowl  whisk together lemon juice  garlic  1/3 cup oil  1 1/2 teaspoons smoked paprika  and 1/2 teaspoon salt. Transfer 1/4 cup dressing to resealable bag  add chicken  and marinate at least 20 minutes  up to 2 hours; reserve remaining dressing.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('6', 'Step 2Meanwhile  toast bread until golden brown  then tear into pieces and set aside.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('6', 'Step 3Heat remaining 2 teaspoons oil in large skillet on medium. Remove chicken from marinade  season with 1/4 teaspoon salt  and cook until golden brown  4 to 5 minutes per side. Transfer to cutting board  sprinkle with remaining 1/4 teaspoon smoked paprika  then slice.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('6', 'Step 4To reserved dressing  add tomatoes  pepperoncini  and parsley and toss to combine.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('6', 'Step 5Arrange lettuce on plates  top with torn sourdough  and top with tomato mixture  avocado  and sliced chicken.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('7', 'Step 1Heat oven to 350Â°F. Spread sesame seeds on rimmed baking sheet and toast until lightly golden (do not let darken)  about 10 minutes. Transfer to second rimmed baking sheet and let cool completely.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('7', 'Step 2In food processorÂ (make sure itâ€™s completely dry before adding ingredients)  process cooled sesame seeds  scraping down sides as needed  until smooth  pourable paste forms  5 to 6 minutes. Refrigerate tahiniÂ in jar or airtight container for up to 4 weeks.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('7', 'Step 1Heat oven to 425Â°F. On rimmed baking sheet  toss onions  squash and fennel with 1 tablespoon oil  thyme and 1/4 teaspoon each salt and pepper; roast 15 minutes.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('7', 'Step 2Meanwhile  heat remaining 2 teaspoons oil in medium skillet on medium. Season chicken with 1/4 teaspoon each salt and pepper and cook  skin side down  until deep golden brown  8 to 10 minutes. Flip and cook 3 minutes more.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('7', 'Step 3Nestle chicken among veggies and roast until chicken is cooked through  15 to 18 minutes. Transfer chicken to cutting board; let rest at least 5 minutes.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('8', 'Step 4Meanwhile  continue roasting vegetables until ready to serve  about 5 minutes. Slice half of chicken and serve with one-third of vegetables. Refrigerate leftover chicken and vegetables for lunch the next day.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('8', 'Step 1Place flour in shallow bowl. In second shallow bowl  beat eggs  garlic  2 tablespoons water and 3/4 teaspoon salt. In third shallow bowl  combine panko with 1/3 cup Parmesan and toss with oil.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('8', 'Step 2Pound each cutlet to even thickness. In large bowl  toss with lemon juice to coat. Dip each cutlet in flour  then in eggs (letting excess drip off)  then in panko mixture  patting to adhere. Transfer to parchment-lined plate.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('8', 'Step 3Heat air fryer to 360Â°F. Brush air fryer basket liberally with oil and add 2 cutlets. Air-fry until beginning to turn golden brown  6 minutes. Using tongs  flip and air-fry until golden brown and cooked through (165Â°F on instant-read thermometer)  7 to 9 minutes more. Transfer to another plate. Repeat with remaining cutlets.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 4Return 2 cooked cutlets to basket and top each with 4 tablespoons marinara  1 tablespoon basil  then with mozzarella and Parmesan. Heat air fryer to 400Â°F. Air-fry until cheese begins to brown  2 to 3 minutes. Repeat with remaining cutlets  marinara  basil  mozzarella  and Parmesan. Sprinkle with additional basil if desired.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 1Heat oven to broil and arrange racks in upper and middle positions. Grease rimmed baking sheet withÂ 1/2 tablespoon oil.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 2In large bowl  stir together zaâ€™atar  2 tablespoons olive oil  and 1/4 teaspoon each salt and pepper. Add chicken and toss to coat. Arrange on prepared sheet and broil on middle rack until beginning to brown  12 to 14 minutes. Transfer sheet to top rack and broil until browned and cooked through  2 to 3 minutes more. Immediately add 1 tablespoon lemon juice to baking sheet  scrape up any browned bits  then flip chicken to coat in pan juices.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 3Meanwhile  in food processor  puree yogurt  feta  lemon zest  1/2 tablespoon lemon juice  and 1/4 teaspoon each salt and pepper until smooth  1 to 2 minutes. Scrape sides of bowl  then add dill and pulse to combine.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 4In large bowl  whisk together remaining 1 1/2 tablespoons oil and 1 tablespoon lemon juice with 1/4 teaspoon each salt and pepper. Add cucumber  scallions  and mint and toss to combine. Spread whipped feta on plates and top with chicken and cucumber salad.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('9', 'Step 1Cook chicken: Heat air fryer to 370Â°F. In shallow bowl  mix panko Â oil  and 1/2 teaspoon each salt and pepper.Â Transfer to piece of foil  fold up sides to create shallow dish  and air-fry  stirring twice through  until crumbs are evenly golden brown  5 to 7 minutes. Return to shallow bowl to cool slightly. Heat air fryer to 400Â°F.', '6');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('10', 'Step 2Place flour in second shallow bowl and beat eggs in third shallow bowl. Season chicken with 1/8 teaspoon each salt and pepper. Working with 1 strip atÂ a time  coat in flour  then egg  thenÂ in toasted panko  pressing gently to help adhere. Transfer to baking sheet.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('10', 'Step 3Working in 3 batches  add coated chicken in single layer  leaving space around each strip. Air-fry  flipping after 6 minutes  until golden brown and crisp Â 9 to 10 minutes total.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('10', 'Step 4Meanwhile  make sauce: In small bowl  stir mayonnaise  barbecue sauce  mustard  honey  lemon juice  and vinegar. Serve with chicken strips.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('10', 'Step 1Heat oven to 375Â°F. Spread 1/4 cup enchilada sauce on bottom of 8-inch round cake pan. Spread 1 tortilla with one-third of refried beans and place in cake pan  bean side up. Top with one-third of chicken (1 cup)  1/4 cup onion  and 1/2 cup cheese. Spoon one-third of remaining enchilada sauce on top (about 2/3 cup).', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('11', 'Step 2Repeat twice more with remaining tortillas  beans  chicken  1/4 cup onion  and 1/2 cup cheese  finishing with final remaining tortilla. Transfer to oven and bake until bubbling  20 to 25 minutes.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('11', 'Step 3Heat broiler. Top with remaining 1/4 cup cheese and broil until cheese melts. Let rest 10 minutes. Sprinkle with remaining 1/4 cup onion  then cilantro  just before serving.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('11', 'Step 1Heat grill to medium.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('11', 'Step 2Rub chicken with 1/2 teaspoon olive oil and sprinkle with 1/4 teaspoon each salt and pepper. Grill until instant-read thermometer registers 165Â°F  8 to 10 minutes per side. Transfer to cutting board and let rest 10 minutes before slicing.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('12', 'Step 3While chicken is grilling  grill lemon halves and baguette  cut sides down  until charred  2 to 3 minutes. Transfer to cutting board. Rub cut side of baguette with garlic clove.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('12', 'Step 4Squeeze 2 tablespoons juice from grilled lemon halves into small bowl and whisk in mayonnaise  Dijon  and anchovy paste  then gradually whisk in remaining 2 tablespoons oil until thoroughly incorporated.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('12', 'Step 5In large bowl  toss romaine and celery with 1/4 cup dressing and 1/4 teaspoon pepper to coat  then toss with Parmesan and another 1/4 teaspoon pepper.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('12', 'Step 6Drizzle remaining dressing on baguette and layer with sliced chicken  salad  and tomatoes. Cut each in half to make 4 sandwiches.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('13', 'Step 1In large bowl  combine oil  lemon juice and zest  3/4 teaspoon salt and 1/4 teaspoon pepper. Add snap peas  chicken  and onion and toss to combine. Fold in mint  parsley  and Parmesan.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('13', 'Step 1 Using vegetable peeler  remove 2 stripslemon zest and add to rice cooker along with garlic  rice and 1/4 teaspoon salt. Fill with water to second line on cooker and stir.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('13', 'Step 2Season chicken with 1/2 teaspoon salt andÂ 1/4 teaspoon pepper and place on steam rack Â then transfer to rice cooker. Cook onÂ white rice setting until programÂ is complete and chicken is cookedÂ through  30 to 60 minutes.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('13', 'Step 3Meanwhile  squeeze 11/2 tablespoon lemonÂ juice into small bowl and whisk in honeyÂ and 1/4 teaspoon each salt and pepper. Stir inÂ scallion and oil  then mint.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('14', 'Step 4Once rice and chicken are ready Â transfer chicken and steam rack to cutting board. Discard lemon zest and garlicÂ cloves. Fluff rice  add greens  close lidÂ and let sit 5 minutes  then fold into rice.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('14', 'Step 5Slice chicken and serve with rice Â dressing  feta and lemon wedges.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('14', 'Step 1In blender  combine vinegars  garlic  mustard  agave  basil  thyme  and 1/2 tsp each salt and pepper; blend until smooth. Add oil and blend on low until just incorporated but not emulsified  about 10 seconds. Stir in red pepper flakes and oregano.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('15', 'Step 2In small baking dish  coat chicken with 1/3 cup dressing and marinate at least 10 minutes at room temperature or up to overnight in refrigerator.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('15', 'Step 3Meanwhile  transfer 3 tablespoons remaining dressing to large bowl. Add peppers  tomatoes and onion and toss to coat.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('15', 'Step 4Heat large skillet on medium and cook chickenÂ until golden brown and cooked through  5 to 7 min.per side.Â Â Transfer to cutting board and let rest 5 minutes before slicing.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('15', 'Step 5Add greens to peppers and toss to coat. Top with Parmesan and serve with chicken.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('15', 'Step 1Heat large cast-iron skillet on medium-high. Season steak with 1/2 teaspoon each salt and pepper. Add oil to skillet  then steak and cook to desired doneness  2 to 3 minutes per side for medium-rare  depending on thickness. Transfer to cutting board and let rest at least 5 minutes before slicing against grain.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('16', 'Step 2Using side of chefâ€™s knife  smash cucumbers slightly to crush  thenÂ cut each half into 4 to 6 chunks.Â Transfer cucumbers to medium bowl and toss with 2 teaspoons salt. Let sit 10 minutes.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('16', 'Step 3Meanwhile  in large bowl  whisk together sesame oil  sesameÂ seeds  soy sauce  lemon juice  ginger  and honey.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('16', 'Step 4Rinse cucumbers in colander  then shake off as much water as possible. Transfer to bowl with dressing and toss to combine  then toss with cilantro. Serve alongside steak and drizzle with chili crisp if desired.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('16', 'Step 1Cook soba 2 minutes less than package directions. Add snap peas and cook until crisp-tender  1 to 2 minutes. Drain soba and snap peas.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('17', 'Step 2Meanwhile  in medium bowl  whisk together orange zest  orange juice  vinegar  ginger  and 1/4 teaspoon salt. Add shallot and let sit 5 minutes.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('17', 'Step 3Heat oil in large nonstick skillet on medium. Season fish with 1/4 teaspoon each salt and pepper  then cook  skin side down  until skin is crisp and golden brown  about 7 minutes. Flip and cook until fish is opaque throughout  2 to 4 minutes more.', '2');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('17', 'Step 4To serve  divide dressing among bowls. Top with soba (rinsing if needed)  snap peas  then fish.', '3');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('17', 'Step 1Cook rice per package directions. Heat 1 tablespoon oil in large nonstick skillet on medium. In large bowl  toss paneer with 1 tablespoon oil  1/2 teaspoon garam masala  1/4 teaspoon ground coriander  and 1/4 teaspoon salt. Add paneer and cook undisturbed until golden brown on bottom  2 to 4 minutes. Toss  then continue to cook until golden brown on other side  2 to 3 minutes more. Transfer paneer to plate.', '4');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('17', 'Step 2Add 1 tablespoon oil to skillet and heat on medium. Add onion and 1/4 teaspoon each salt and pepper and cook  covered  stirring occasionally  until beginning to soften  5 minutes. Add ginger  garlic  and serrano and cook  stirring  1 minute. Add remaining 1 1/2 teaspoons ground coriander  1 teaspoon garam masala and ground cumin and cook  stirring  1 minute more.', '5');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('18', 'Step 3Whisk 1 teaspoon vegetable bouillon base into 1 cup warm water; add to skillet along with spinach and 1/4 teaspoon salt. Cover and cook  stirring occasionally  until spinach is very soft  6 minutes. Remove from heat  then stir in 1 cup heavy cream until incorporated. Fold in paneer. Serve with rice and naan.', '1');
INSERT INTO steps (Recipe_id, step_description, sequence_number) VALUES ('18', 'Step 1Place flour in shallow bowl. In second shallow bowl  beat eggs  garlic  2 tablespoons water and 3/4 teaspoon salt. In third shallow bowl  combine panko with 1/3 cup Parmesan and toss with oil.', '2');
